<template>
    <div class="cards">

<div id="bg">
  <br><br>
      <h1>Industrial Training</h1>   
  <br><br>
      <h4>{{$store.state.training.card.length}} Courses | {{duration}} Hours | {{lessons}} lessons</h4>
      <br>
      <h4>Our goal is to be the #1 learning resource for Training courses</h4>
  <br><br><br>
</div>


<div id="course">
  <v-container class="lighten-5">

      <v-row no-gutters
      >
        <v-col
          v-for="(n,i) in $store.state.training.card"
          :key="i"
          cols="12"
          sm="4"
          class="mt-8 pa-2"
          align-self="center"
        >
          <course
              :title="n.title"
              :body="n.body"
              :link="'/training/'+n.title"
              :image="n.image"
              :time="n.duration"
              :lessons="n.lessons"/>
        </v-col>
      </v-row>
  </v-container>
</div>

  <br> <br> <br> <br> <br>
    </div>
</template>


<script>
import cardVue from '../../components/card.vue'
import axios from 'axios'

export default {
    components: {
        course: cardVue,
    },
    data(){
        return{
          duration: null,
          lessons: null,
          course: null
        };
    },

  created() {
     axios.get('https://sudans-tech.firebaseio.com/training.json')
       .then(res=>{
         var result = Object.keys(res.data).map((key) => [res.data[key]]);
         this.$store.state.training.card =[]
         for (let index =0; index < result.length; index++){
           this.$store.state.training.card.push(result[index][0])
         }
        var total = 0
        var total_1 = 0
        for (let index = 0; index < this.$store.state.training.card.length; index++) {
          total += parseInt(this.$store.state.training.card[index].duration) 
          total_1 += parseInt(this.$store.state.training.card[index].lessons) 
        }
        this.duration=total
        this.lessons=total_1
       })
  }
}
</script>


